	SELECT workflow.workflow_type FROM vap_workflow workflow WHERE workflow.workflow_id=NEW.workflow_id INTO var_workflow_type;
   
	IF var_workflow_type = 'Product Workflow'  THEN
      
		SELECT workflow.product_id FROM vap_workflow workflow WHERE workflow.workflow_id=NEW.workflow_id INTO var_product_id;
		SELECT product.process_id FROM vap_workflow workflow INNER JOIN vap_products product ON workflow.product_id = product.product_id WHERE workflow.workflow_id=NEW.workflow_id INTO var_process_id;
         
		IF NEW.title = 'DeviceMarketingReview'  THEN
		UPDATE vap_product_process_info SET device_marketing_input_status = 'NOT_PROCESSED' WHERE process_id = var_process_id;
           
		ELSEIF NEW.title = 'ProductInfoUpload'  THEN
		UPDATE vap_product_process_info SET product_info_input_status = 'NOT_PROCESSED' WHERE process_id = var_process_id;
        
		ELSEIF NEW.title = 'ExportCompliance'  THEN
		UPDATE vap_product_process_info SET export_compliance_input_status  = 'NOT_PROCESSED' WHERE process_id = var_process_id;
         
		ELSEIF NEW.title = 'UploadPDFofSampleProduct'  THEN
		UPDATE vap_product_process_info SET upload_pdf_input_status = 'NOT_PROCESSED' WHERE process_id = var_process_id;
		END IF; 
		
		IF NEW.title = 'Approved'  THEN
		UPDATE vap_products SET `status` = 'Approved' WHERE product_id = var_product_id;
		END IF; 
		
	END IF;
END */$$


DELIMITER ;

