DELIMITER $$

/*!50003 DROP TRIGGER*//*!50032 IF EXISTS */ /*!50003 `vap_workitem_update_trg` */$$

/*!50003 CREATE */ /*!50017 DEFINER = 'deviceportaluser'@'%' */ /*!50003 TRIGGER `vap_workitem_update_trg` AFTER UPDATE ON `vap_workitem` FOR EACH ROW BEGIN
	DECLARE var_workflow_type VARCHAR(50);
	DECLARE var_process_id INTEGER;
	DECLARE var_product_id INTEGER;
	DECLARE var_company_id INTEGER;
	DECLARE var_workitemcount INTEGER;
	DECLARE var_workflow_inprogress_count INTEGER;
	
        INSERT INTO vsap_audit.vap_workitem$aud (workitem_id,title,display_title,STATUS,allowed_roles,require_input,next_actions,workflow_id,start_date,end_date,is_active,created_date,created_by,updated_date,updated_by,VERSION,action_taken,is_locked,AUD_ACTION,AUD_TIMESTAMP,AUD_USER) 
