	SELECT workflow.workflow_type FROM vap_workflow workflow WHERE workflow.workflow_id=NEW.workflow_id INTO var_workflow_type;
   
	IF var_workflow_type = 'Product Workflow' AND OLD.status = 'InProgress' AND NEW.status = 'Processed' THEN
      
		SELECT workflow.product_id FROM vap_workflow workflow WHERE workflow.workflow_id=NEW.workflow_id INTO var_product_id;
		SELECT product.process_id FROM vap_workflow workflow INNER JOIN vap_products product ON workflow.product_id = product.product_id WHERE workflow.workflow_id=NEW.workflow_id INTO var_process_id;
         
		IF NEW.title = 'DeviceMarketingReview'  THEN
		UPDATE vap_product_process_info SET device_marketing_input_status = 'PROCESSED' WHERE process_id = var_process_id;
           
		ELSEIF NEW.title = 'ProductInfoUpload'  THEN
		UPDATE vap_product_process_info SET product_info_input_status = 'PROCESSED' WHERE process_id = var_process_id;
        
		ELSEIF NEW.title = 'ExportCompliance'  THEN
		UPDATE vap_product_process_info SET export_compliance_input_status  = 'PROCESSED' WHERE process_id = var_process_id;
         
		ELSEIF NEW.title = 'UploadPDFofSampleProduct'  THEN
		UPDATE vap_product_process_info SET upload_pdf_input_status = 'PROCESSED' WHERE process_id = var_process_id;
		END IF; 
		
		
		IF NEW.action_taken IS NOT NULL AND NEW.action_taken = 'Deny'  THEN
		UPDATE vap_products SET `status` = 'Denied' WHERE product_id = var_product_id;
		ELSEIF NEW.action_taken IS NOT NULL AND NEW.action_taken = 'RFI'  THEN
		UPDATE vap_products SET `status` = 'RFI' WHERE product_id = var_product_id;
		ELSE
			SELECT COUNT(*) FROM vap_workitem WHERE workflow_id = NEW.workflow_id AND STATUS = 'InProgress' AND title IN ('DeviceMarketingReviewDenied', 'ExportComplianceReviewDenied', 'RequirementsGroupReviewDenied', 'DeviceComplianceReviewDenied', 'DeviceComplianceReviewEOTDenied', 'DeviceMarketingFinalReviewDenied', 'DeviceMarketingReviewRFI', 'ExportComplianceReviewRFI', 'RequirementsGroupReviewRFI', 'DeviceComplianceReviewRFI', 'DeviceComplianceReviewEOTRFI', 'DeviceMarketingFinalReviewRFI') INTO var_workitemcount;
			SELECT COUNT(*) FROM vap_workitem WHERE workflow_id = NEW.workflow_id AND STATUS = 'InProgress' INTO var_workflow_inprogress_count;
			IF var_workitemcount = 0  THEN
			UPDATE vap_products SET `status` = 'Submitted' WHERE product_id = var_product_id;
			END IF; 
			IF var_workflow_inprogress_count = 0  THEN
			UPDATE vap_products SET `status` = 'Approved' WHERE product_id = var_product_id;
			END IF; 
		END IF; 
	ELSEIF var_workflow_type = 'Partner Workflow'  THEN
		SELECT workflow.company_id FROM vap_workflow workflow WHERE workflow.workflow_id=NEW.workflow_id INTO var_company_id;
		IF NEW.action_taken IS NOT NULL AND NEW.action_taken = 'Suspend'  THEN
		UPDATE vap_company SET is_suspended = TRUE WHERE company_id = var_company_id;
		ELSEIF NEW.action_taken IS NOT NULL AND NEW.action_taken = 'UnSuspend'  THEN
		UPDATE vap_company SET is_suspended = FALSE WHERE company_id = var_company_id;
		END IF; 
	END IF;
END */$$


