/*!50003 DROP FUNCTION IF EXISTS `get_work_item_end_date` */;
DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` FUNCTION `get_work_item_end_date`(p_product_id INT, p_work_item_name TEXT) RETURNS text CHARSET utf8
    DETERMINISTIC
BEGIN
  DECLARE v_end_date TEXT;
  
  DECLARE done INT DEFAULT FALSE;
  DECLARE csr CURSOR FOR 
  SELECT wi.end_date
	FROM vap_products prod, vap_workflow wf, vap_workitem wi 
	WHERE prod.product_id=wf.product_id 
	AND wf.workflow_id = wi.workflow_id 
	AND (wi.action_taken IN ('Approve', 'Submit to Verizon', 'Accept Agreement and Submit to Verizon', 'Start Testing', 'End Testing') AND wi.title=p_work_item_name)
	AND prod.product_id=p_product_id ORDER BY workitem_id;
  DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = TRUE;
  
  SET v_end_date = '';
  
  OPEN csr;
  read_loop: LOOP
    FETCH csr INTO v_end_date;
    IF done THEN
      LEAVE read_loop;
    END IF;
  END LOOP;
  CLOSE csr;
  RETURN v_end_date;
END */$$
