BEGIN
	DECLARE blocked_domain_name_variable VARCHAR(250);
	DECLARE domain_name_variable VARCHAR(250);
		
	SELECT company_domain INTO domain_name_variable 
	FROM vap_company 
	WHERE company_id = company_id_param AND is_active = '1';
	
	IF NOT (domain_name_variable IS NULL OR TRIM(domain_name_variable) = '') THEN
	
		SELECT DISTINCT vbd.domain_name INTO blocked_domain_name_variable 
		FROM vap_company AS c 
		INNER JOIN vap_blocked_domain AS vbd ON c.company_domain = vbd.domain_name 
		WHERE c.company_id = company_id_param;
		
