			UPDATE vap_workflow SET is_active = '0', updated_by = username,updated_date = SYSDATE() WHERE company_id = company_id_param AND is_active = '1';
			UPDATE vap_user SET is_active = '0', updated_by = username,updated_date = SYSDATE() WHERE company_id = company_id_param AND is_active = '1';
			
			INSERT INTO vap_blocked_domain (domain_name, created_date, created_by, updated_date, updated_by) 
