CREATE DATABASE /*!32312 IF NOT EXISTS*/`vzdnBlog_audit` /*!40100*/ DEFAULT CHARACTER SET utf8 ;

GRANT ALL ON vzdnBlog.* TO 'vzdnBlog_audit'@'%' IDENTIFIED BY 'vzdnBlog_audit';

USE vzdnBlog_audit;

CREATE TABLE autoping$aud ENGINE = INNODB AS SELECT id,websiteid,pingtargetid,'ABC' AUD_ACTION,CURRENT_TIMESTAMP AUD_TIMESTAMP,CURRENT_USER() AUD_USER FROM vzdnBlog.autoping WHERE 1 = 0;	
