CREATE TABLE rag_properties$aud ENGINE = INNODB AS SELECT name,value,'ABC' AUD_ACTION,CURRENT_TIMESTAMP AUD_TIMESTAMP,CURRENT_USER() AUD_USER FROM vzdnBlog.rag_properties WHERE 1 = 0;	
CREATE TABLE rag_subscription$aud ENGINE = INNODB AS SELECT id,title,feed_url,site_url,author,last_updated,inbound_links,inbound_blogs,'ABC' AUD_ACTION,CURRENT_TIMESTAMP AUD_TIMESTAMP,CURRENT_USER() AUD_USER FROM vzdnBlog.rag_subscription WHERE 1 = 0;	
