CREATE TABLE roller_hitcounts$aud ENGINE = INNODB AS SELECT id,websiteid,dailyhits,'ABC' AUD_ACTION,CURRENT_TIMESTAMP AUD_TIMESTAMP,CURRENT_USER() AUD_USER FROM vzdnBlog.roller_hitcounts WHERE 1 = 0;	
