CREATE TABLE webpage$aud ENGINE = INNODB AS SELECT id,name,description,link,websiteid,template,updatetime,hidden,navbar,templatelang,decorator,outputtype,action,'ABC' AUD_ACTION,CURRENT_TIMESTAMP AUD_TIMESTAMP,CURRENT_USER() AUD_USER FROM vzdnBlog.webpage WHERE 1 = 0;	
CREATE TABLE website$aud ENGINE = INNODB AS SELECT id,name,handle,description,userid,defaultpageid,weblogdayid,ignorewords,enablebloggerapi,editorpage,bloggercatid,defaultcatid,allowcomments,emailcomments,emailfromaddress,emailaddress,editortheme,locale,timezone,defaultplugins,isenabled,isactive,datecreated,blacklist,defaultallowcomments,defaultcommentdays,commentmod,displaycnt,lastmodified,pagemodels,enablemultilang,showalllangs,customstylesheet,about,icon,'ABC' AUD_ACTION,CURRENT_TIMESTAMP AUD_TIMESTAMP,CURRENT_USER() AUD_USER FROM vzdnBlog.website WHERE 1 = 0;	

GRANT ALL ON vzdnBlog_audit.* TO 'vzdnBlog'@'%' IDENTIFIED BY 'vzdnBlog';
GRANT ALL ON vzdnBlog.* TO 'vzdnBlog'@'%' IDENTIFIED BY 'vzdnBlog';
